--
-- Create procedure `Proc_Property_PagingAndFilter`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_Property_PagingAndFilter (IN pageNumber int,
IN pageSize int,
IN p_PropertyType varchar(255),
IN p_DepartmentName varchar(255),
IN p_SearchInput varchar(255),
IN p_ExcludedIds varchar(255) -- Thêm tham số danh sách ID cần loại trừ
)
BEGIN
  DECLARE startRow int;
  SET startRow = (pageNumber - 1) * pageSize;

  SELECT
    p.PropertyId,
    p.PropertyCode,
    p.PropertyName,
    p.Quantity,
    p.OriginalPrice,
    p.NumberYearUse,
    p.WearRate,
    p.WearRateValue,
    p.FollowYear,
    p.PurchaseDate,
    p.FollowDate,
    p.CreatedDate,
    p.CreatedBy,
    p.ModifiedDate,
    p.ModifiedBy,
    d.DepartmentId,
    d.DepartmentCode,
    d.DepartmentName,
    p1.PropertyTypeId,
    p1.PropertyTypeCode,
    p1.PropertyTypeName
  FROM property p
    LEFT JOIN department d
      ON p.DepartmentId = d.DepartmentId
    LEFT JOIN propertytype p1
      ON p.PropertyTypeId = p1.PropertyTypeId
  WHERE (p_SearchInput IS NULL
  OR p_SearchInput = ''
  OR p.PropertyCode LIKE CONCAT('%', p_SearchInput, '%')
  OR p.PropertyName LIKE CONCAT('%', p_SearchInput, '%'))
  AND (p_DepartmentName IS NULL
  OR p_DepartmentName = ''
  OR d.DepartmentName LIKE CONCAT('%', p_DepartmentName, '%'))
  AND (p_PropertyType IS NULL
  OR p_PropertyType = ''
  OR p1.PropertyTypeName LIKE CONCAT('%', p_PropertyType, '%'))
  AND (p_ExcludedIds IS NULL
  OR p_ExcludedIds = ''
  OR LOCATE(p.PropertyId, p_ExcludedIds) = 0) -- Loại trừ các ID trong danh sách
  ORDER BY p.PropertyCode DESC
  LIMIT startRow, pageSize;
END
$$

