--
-- Create procedure `Proc_Property_CheckDuplicateCode`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_Property_CheckDuplicateCode (IN PropertyCode varchar(100), IN PropertyId char(36))
BEGIN
  DECLARE Count int;

  -- Kiểm tra giá trị của PropertyId và thực hiện truy vấn tương ứng
  IF PropertyId IS NOT NULL
    OR PropertyId NOT LIKE ''
    OR PropertyId != '00000000-0000-0000-0000-000000000000' THEN
    SELECT
      count(*) INTO Count
    FROM property p
    WHERE p.PropertyCode = PropertyCode
    AND p.PropertyId != PropertyId;
    SELECT
      IF(Count > 0, TRUE, FALSE) AS IsDuplicate;
  ELSE
    SELECT
      count(*) INTO count
    FROM property p
    WHERE p.PropertyCode = PropertyCode;
    SELECT
      IF(count > 0, TRUE, FALSE) AS IsDuplicate;
  END IF;

END
$$

