-- Create procedure `Proc_TransferAsset_CheckDuplicateCode`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_TransferAsset_CheckDuplicateCode (IN TransferAssetCode varchar(100), IN TransferAssetId char(36))
BEGIN
  DECLARE Count int;

  -- Kiểm tra giá trị của PropertyId và thực hiện truy vấn tương ứng
  IF TransferAssetId IS NOT NULL
    OR TransferAssetId NOT LIKE ''
    OR TransferAssetId != '00000000-0000-0000-0000-000000000000' THEN
    SELECT
      count(*) INTO Count
    FROM transfer_asset ta
    WHERE ta.TransferAssetCode = TransferAssetCode
    AND ta.TransferAssetId != TransferAssetId;
    SELECT
      IF(Count > 0, TRUE, FALSE) AS IsDuplicate;
  ELSE
    SELECT
      count(*) INTO count
    FROM transfer_asset ta
    WHERE ta.TransferAssetCode = TransferAssetCode;
    SELECT
      IF(count > 0, TRUE, FALSE) AS IsDuplicate;
  END IF;

END
$$

