--
-- Create procedure `Proc_TransferAsset_CheckDelete`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_TransferAsset_CheckDelete (IN listId text)
COMMENT 'Thủ tục check tài sản được phép xóa hay không'
BEGIN
  DROP TEMPORARY TABLE IF EXISTS temp_transfer_assets;

  -- Tạo bảng tạm lưu thông tin tài sản trong các chứng từ (listId là danh sách Id các chứng từ)
  CREATE TEMPORARY TABLE temp_transfer_assets AS
  SELECT
    ta.TransferAssetId,
    ta.TransferAssetCode,
    tad.PropertyId,
    ta.TransferDate,
    p.PropertyCode
  FROM transfer_asset ta
    LEFT JOIN transfer_asset_detail tad
      ON ta.TransferAssetId = tad.TransferAssetId
    LEFT JOIN property p
      ON tad.PropertyId = p.PropertyId
  WHERE LOCATE(ta.TransferAssetId, listId) > 0;

  -- Tạo bảng tạm lưu bản ghi về thông tin tài sản có ở bảng tạm temp_transfer_assets mà 
  -- thuộc chứng từ khác có thời gian điều chuyển lớn hơn ở chứng từ của tài sản hiện tại
  DROP TEMPORARY TABLE IF EXISTS temp_result_max;
  CREATE TEMPORARY TABLE temp_result_max AS
  SELECT
    tta.TransferAssetCode,
    tta.PropertyId,
    tta.PropertyCode AS TransferAssetCode_ta,
    ta.TransferDate
  FROM temp_transfer_assets tta
    LEFT JOIN transfer_asset ta
      ON tta.TransferAssetId = ta.TransferAssetId
  WHERE tta.PropertyId IN (SELECT
      tad.PropertyId
    FROM transfer_asset ta
      LEFT JOIN transfer_asset_detail tad
        ON ta.TransferAssetId = tad.TransferAssetId
    WHERE tta.TransferDate < ta.TransferDate)
  ORDER BY ta.TransferDate DESC, TransferAssetCode_ta DESC LIMIT 1;

  -- Lấy ra thông tin của tất cả chứng từ có thông tin tài sản là thông tin được lưu ở bảng tạm temp_result_max
  -- và có ngày điều chuyển lớn hơn ngày điều chuyển của thông tin ở bảng temp_result_max
  SELECT
    ta.TransferAssetCode,
    p.PropertyCode,
    ta.TransferDate,
    trm.TransferAssetCode AS TransferAssetCheckedCode
  FROM transfer_asset ta
    LEFT JOIN transfer_asset_detail tad
      ON ta.TransferAssetId = tad.TransferAssetId
    LEFT JOIN property p
      ON p.PropertyId = tad.PropertyId
    LEFT JOIN temp_result_max trm
      ON trm.PropertyId = p.PropertyId
  WHERE ta.TransferDate > trm.TransferDate
  ORDER BY ta.TransferAssetCode DESC;
END
$$

--
-- Create procedure `Proc_Property_GetCurrent`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_Property_GetCurrent (IN PageNumber int, IN PageSize int, IN SearchInput varchar(255), IN ExcludedIds text)
COMMENT 'Lấy ra danh sách tài sản hiện tại'
BEGIN
  DECLARE startRow int;
  DECLARE recordCount int;
  DECLARE totalPrice decimal(18, 4);
  DECLARE totalResidualPrice decimal(18, 4);
  SET startRow = (PageNumber - 1) * PageSize;
  -- Create a temporary table to store property transactions
  DROP TABLE IF EXISTS property_new;

  CREATE TABLE property_new (
    PropertyCode varchar(255),
    TransactionDate datetime
  );

  -- Insert data into the temporary table
  INSERT INTO property_new (PropertyCode, TransactionDate)
    SELECT
      p.PropertyCode,
      MAX(ta.TransactionDate) AS TransactionDate
    FROM transfer_asset_detail tad
      LEFT JOIN transfer_asset ta
        ON ta.TransferAssetId = tad.TransferAssetId
      LEFT JOIN property p
        ON tad.PropertyId = p.PropertyId
    GROUP BY p.PropertyCode;


  SELECT
    COUNT(*),
    SUM(p.OriginalPrice),
    SUM(p.OriginalPrice - p.WearRateValue * (YEAR(CURDATE()) - YEAR(p.FollowDate))) INTO recordCount, totalPrice, totalResidualPrice
  FROM property p
  WHERE (ExcludedIds IS NULL
  OR ExcludedIds = ''
  OR LOCATE(CONVERT(p.PropertyId, char), ExcludedIds) = 0)
  AND (SearchInput IS NULL
  OR SearchInput = ''
  OR p.PropertyCode LIKE CONCAT('%', SearchInput, '%')
  OR p.PropertyName LIKE CONCAT('%', SearchInput, '%'));

  SELECT
    tad.TransferAssetDetailId,
    tad.DepartmentTransferId,
    p.PropertyId,
    tad.DepartmentTransferName,
    p.PropertyCode,
    p.PropertyName,
    p.OriginalPrice,
    p.FollowYear,
    p.FollowDate,
    p.NumberYearUse,
    p.WearRateValue,
    p.DepartmentId,
    d.DepartmentName,
    (p.OriginalPrice - p.WearRateValue * (YEAR(CURDATE()) - YEAR(p.FollowDate))) AS ResidualPrice,

    recordCount AS TotalRecordCount,
    TotalPrice AS TotalPrice,
    TotalResidualPrice AS TotalResidualPrice
  FROM property p
    LEFT JOIN transfer_asset_detail tad
      ON p.PropertyId = tad.PropertyId
    LEFT JOIN transfer_asset ta
      ON ta.TransferAssetId = tad.TransferAssetId
    LEFT JOIN department d
      ON p.DepartmentId = d.DepartmentId

  WHERE ((p.PropertyCode, ta.TransactionDate) IN (SELECT
      PropertyCode,
      TransactionDate
    FROM property_new)
  OR p.PropertyCode NOT IN (SELECT
      PropertyCode
    FROM property_new))
  AND (ExcludedIds IS NULL
  OR ExcludedIds = ''
  OR LOCATE(CONVERT(p.PropertyId, char), ExcludedIds) = 0)  -- Loại trừ các ID trong danh sách
  AND (SearchInput IS NULL
  OR SearchInput = ''
  OR p.PropertyCode LIKE CONCAT('%', SearchInput, '%')
  OR p.PropertyName LIKE CONCAT('%', SearchInput, '%'))
  ORDER BY p.PropertyCode DESC
  LIMIT startRow, PageSize;
  -- Drop the temporary table
  DROP TABLE property_new;

END
$$

